```
/*
Solution:
1. 2 Pointer Approach with Greedy expansion.
2. Start left and right at k and expand it towards both extremes until the input array nums is exhausted.
3. As we go over array, try to mainatin the minVal and maxRes for each iteration

TC -> O(N)
SC -> O(1)

*/

class Solution {
public:
    int maximumScore(vector<int>& nums, int k) {
        int left = k, right = k;
        int minVal = nums[k];
        int res = nums[k];
        int nSize = nums.size();

        while (left > 0 || right < nSize - 1) {
            int leftVal = (left > 0 ? nums[left - 1] : 0);
            int rightVal = (right < nSize - 1 ? nums[right + 1] : 0);

            // Go right
            if (leftVal < rightVal) {
                right++;
                minVal = min(minVal, nums[right]);
            }
            // Go Left
            else {
                left--;
                minVal = min(minVal, nums[left]);
            }

            res = max(res, minVal * (right - left + 1));
        }

        return res;
    }
};
```

