class Solution {
public:
    int maximumScore(vector<int>& nums, int k) {
        int len = nums.size();
        vector<int> arr(len, 0 );

        int i = k-1, j = k+1;
        int currMin = nums[k];

        arr[k] = nums[k];
        while( i >= 0 ) {
            currMin = min(currMin, nums[i]);
            arr[i--] = currMin;
        }

        currMin = nums[k];
        while( j < len ) {
            currMin = min(currMin, nums[j]);
            arr[j++] = currMin;
        }

        int result = arr[k];
        i = 0, j = len-1;
        while( i <= k && j >= k ) {
            result = max(result, min(arr[i], arr[j]) * (j-i+1) );
            if( j == k || arr[i] <= arr[j] )
                i++;
            else if( i == k || arr[i] > arr[j] )
                j--;
        }  

        return result;
    }
};
```

