public int largestRectangleArea(int[] heights, int k) {

	int max = 0, i = 0, height = heights.length;
	Stack<Integer> s = new Stack<>();

	while (i < height) {

		if (s.isEmpty() || heights[s.peek()] <= heights[i]) {
			s.push(i);
			i++;
		} else {

			int pb = heights[s.pop()];

			int r = i - 1;
			int l = 0;

			if (!s.isEmpty())
				l = s.peek() + 1;

			int area = (r - l + 1) * pb;

			if (area > max && k >= l && k <= r)
				max = area;
		}
	}

	while (!s.isEmpty()) {

		int pb = heights[s.pop()];

		int r = heights.length - 1;
		int l = 0;

		if (!s.isEmpty())
			l = s.peek() + 1;

		int area = (r - l + 1) * pb;

		if (area > max && k >= l && k <= r)
			max = area;
	}

	return max;
}

