        if (ss == se) {
            st[si] = arr[ss]; return;
        }
        int mid = (ss + se) / 2;
        buildTree(2 * si, ss, mid);
        buildTree((2 * si) + 1, mid + 1, se);
        st[si] = min(st[2 * si] , st[(2 * si) + 1]);
    }

    int answerQuery(int si, int ss, int se, int qs, int qe) {

        if (qs > se || qe < ss) return INT_MAX;

        if (qs <= ss && qe >= se) {
            return st[si];
        }
        int mid = (ss + se) / 2;
        int l = answerQuery(2 * si, ss, mid, qs, qe);
        int r = answerQuery((2 * si) + 1, mid + 1, se, qs, qe);

        return min(l, r);
    }
public:
    int maximumScore(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> v(n + 1);
        map<int, vector<int>> indexs;

        for (int i = 1; i <= n; i++) {
            int x = nums[i - 1];
            arr[i] = x;
            v[i] = x;
            indexs[x].pb(i);
        }
        // int k;cin>>k;
        k++;
        buildTree(1, 1, n);
        int maxi = 0, l = 1, r = n;

        auto pred = [&](int mid, vector<int>&dummy) {
            return (dummy[mid] <= k);
        };
        //int op = 100;
        while (true) {
            //cout << l << " " << r << endl;
            if (l > k || r < k) break;
            int mini = answerQuery(1, 1, n, l, r), len = r - l + 1;
            maxi = max(maxi, mini * len);
            if (r == l) break;
            int remove = mini;
            vector<int> dummy = indexs[remove];

            int left = -1, right = dummy.size();
            while (right - left > 1) {
                int mid = (left + right + 1) / 2;
                (pred(mid, dummy)) ? (left = mid) : (right = mid);
            }
            int smaller;
            if (left == -1) { 
                smaller = l;
            } else {     
                smaller = dummy[left] + 1;
            }
            int larger;
            if (right == dummy.size()) {
                larger = r;
            } else {
                larger = dummy[right] - 1;
            }
            // deb(smaller);deb(larger);
            l = max(smaller, l), r = min(larger, r);
        }
        return maxi;
    }
};
