# Maximum area in histogram

```
class Solution{
public:
    int maximumScore(vector<int> &nums, int k)
    {

        // Add a 0 to the end of the 'nums' vector
        nums.push_back(0);
        // Initialize a variable 'n' to store the size of the 'nums' vector
        int n = nums.size();
        // Initialize a variable 'i' to keep track of the current index in 'nums'
        int i = 0;
        // Initialize a variable 'maxx' to store the maximum score
        int maxx = 0;
        // Create a stack 'st' to store the indices of the integers in 'nums'
        stack<int> st;
        // While loop to iterate through all elements in 'nums'
        while (i < n)
        {
            // If the stack 'st' is empty or the current element in 'nums' is greater than or equal to the last element in 'st'
            if (st.empty() || nums[i] >= nums[st.top()])
            {
                // Push the current index 'i' into the stack 'st'
                st.push(i++);
            }
            // Else if the current element in 'nums' is less than the last element in 'st'
            else
            {
                // Pop the last element in 'st' and store it in the variable 'x'
                int x = st.top();
                st.pop();
                // Check if the subarray with the minimum element at index 'k' is within the current boundaries of the subarray
                // and update 'maxx' with the maximum value between the previous value of 'maxx' and the product of the popped element 'x'
                // and the length of the subarray
                if ((st.empty() ? 0 : st.top() + 1) <= k && k <= i - 1)
                {
                    maxx = max(maxx, nums[x] * (st.empty() ? i : i - st.top() - 1));
                }
            }
        }
        // Return the maximum score
        return maxx;
    }
};
