class Solution{
public:
    int maximumScore(vector<int> &nums, int k)
    {
        // Initialize a variable 'mn' to store the minimum element in the subarray
        int mn = nums[k];
        // Initialize two variables 'i' and 'j' to store the left and right boundaries of the subarray
        int i = k, j = k;
        // Initialize a variable 'res' to store the maximum score
        int res = nums[k];
        // Do-while loop to iterate through all possible subarrays with the minimum element at index 'k'
        do
        {
            // Check if the left boundary 'i' is greater than 0 and the right boundary 'j' is less than the last index of 'nums'
            if (i > 0 && j < nums.size() - 1)
            {
                // If the element at 'i-1' is greater than or equal to the element at 'j+1', move the left boundary 'i' to the left
                if (nums[i - 1] >= nums[j + 1])
                {
                    --i;
                }
                // Else move the right boundary 'j' to the right
                else
                {
                    ++j;
                }
            }
            // Else if the left boundary 'i' is at the first index of 'nums' and the right boundary 'j' is less than the last index of 'nums'
            else if (i == 0 && j < nums.size() - 1)
            {
                // Move the right boundary 'j' to the right
                ++j;
            }
            // Else if the right boundary 'j' is at the last index of 'nums' and the left boundary 'i' is greater than 0
            else if (j == nums.size() - 1 && i > 0)
            {
                // Move the left boundary 'i' to the left
                --i;
            }
            // Update the variable 'mn' with the minimum value between the current elements at 'i' and 'j' and the previous value of 'mn'
            mn = min({mn, nums[i], nums[j]});
            // Update the variable 'res' with the maximum value between the previous value of 'res' and the product of 'mn' and the length of the subarray
            res = max(res, mn * (j - i + 1));
        }
        // Continue the do-while loop until the left boundary 'i' is greater than 0 or the right boundary 'j' is less than the last index of 'nums'
        while (i > 0 || j < nums.size() - 1);
        // Return the maximum score
        return res;
    }
};
