class Solution {
public:
    int maximumScore(vector<int>& nums, int k) {
        int lowest = nums[k], ans = nums[k];
        int l = k, r = k;
        int n = nums.size();
        while(l >= 0 || r <= n - 1) {
            while(l > 0) {
                if(nums[l - 1] < lowest) break;
                --l;
            }
            while(r < n - 1) {
                if(nums[r + 1] < lowest) break;
                ++r;
            }
            ans = max(ans, (r - l + 1) * lowest);
            if(l == 0 && r != n - 1) {
                lowest = nums[r + 1];
            } else if(l == 0 && r == n - 1) {
                break;
            } else if(l != 0 && r == n - 1) {
                lowest = nums[l - 1];
            } else {
                lowest = max(nums[l - 1], nums[r + 1]);
            }
        }
        return ans;
    }
};
```

