class Solution {
public:

    int sparse[200018][18];
    int log_[200018];

    void build_log(){
        log_[1]=0;
        for (int i = 2; i < 200018; i++) {
            log_[i]=log_[i>>1]+1;
        }
    }

    void build(vector<int>nums,int n){
        for (int i = 0; i < n; i++) {
            sparse[i][0]=nums[i];
        }
        for (int j = 1; j < 18; j++) {
            for (int i = 0; i+(1<<j)-1< n; i++) {
                sparse[i][j]=min(sparse[i][j-1],sparse[i+(1<<(j-1))][j-1]);
            }
        }
    }

    int query(int x,int y){
        int have=log_[y-x+1];
        return min(sparse[x][have],sparse[y-(1<<have)+1][have]);

    }

    int maximumScore(vector<int>& nums,int k) {
        build_log();
        int n=nums.size();
        build(nums,n);
        int left=0,right =n - 1;
        int score = 0;
        while (left <= k && k<= right){
            score = max(score, query(left,right) * (right - left + 1));
            if (query(left,k) <= query(k,right)){
