int maximumScore(vector<int>& nums, int k) {
    int n = nums.size();

    int mini = nums[k];
    for(int i = k + 1; i< n; ++i) {
        nums[i] = std::min(mini, nums[i]);
        mini = nums[i];
    }

    mini = nums[k];
    for(int i = k - 1; i >= 0; --i) {
        nums[i] = std::min(mini, nums[i]);
        mini = nums[i];
    }

    int res = nums[k];
    for(int i = k + 1; i< n; ++i) { // scan right arry start at k
        int current = nums[i] * (i - k + 1);
        res = std::max(current, res);
    }

    for(int i = k - 1; i >= 0; --i) { // scan left array end at k
        int current = nums[i] * (k - i + 1);
        res = std::max(current, res);
    }

    int left = k;
    int right = k;
    while(left >= 0 && right < n) { // scan array to both end from k // the rule is extend left or right which has larger value
        int current = std::min(nums[left], nums[right]) * (right - left + 1);
        res = std::max(res, current);

        int l1 = left;
        while (left > 0 && nums[left - 1] == nums[left]) {
            --left;
        }
        if (left < l1) continue;

        int r1 = right;
        while (right < n - 1 && nums[right + 1] == nums[right]) {
            ++right;
        }
        if (right > r1) continue;

        if (left == 0 && right == n) break;

        if (left == 0) {
            ++right;
            continue;
        }

        if (right == n) {
            --left;
            continue;
        }

        if (nums[left - 1] > nums[right + 1]) {
            --left;
        } else {
            ++right;
        }

    }

    return res;
}

