class Solution {
public:
    int maximumScore(vector<int>& A, int k) {
        int n = A.size();
        int MOD = 1e9 + 7;
        vector<int> left(n), right(n);
        stack<int>st;
        st.push(0);
        left[0] = 1; 
        for(int i=1; i<n; i++)
        {
            while(!st.empty() && A[i] < A[st.top()]) 
                st.pop();

            if(st.empty()) 
                left[i] = i+1; 
            else 
                left[i] = i - st.top(); 

            st.push(i);

        }

        while(st.size()) 
            st.pop();


        st.push(n-1);
        right[n-1] = 1; 
        for(int i=n-2; i>=0; i--)
        {
            while(!st.empty() && A[i] <= A[st.top()]) 
                st.pop();

            if(st.empty()) 
                right[i] = n-i; 
            else 
                right[i] = st.top()-i;

            st.push(i);
        }
        int ans=0;


        for(int i=0;i<n;i++){
            int lr=i-left[i]+1;
            int rr=i+right[i]-1;
            // cout<<lr<<" "<<rr<<endl;
            if(k>rr||k<lr)continue;
            else{
                ans=max(ans,A[i]*(rr-lr+1));
                // if(ans>prev){
                //     prev=ans;
                //     // cout<<i<<" ";
                // }

            }
        }
        return ans;
    }
};
```

