class Solution {
public:
    int maximumScore(vector<int>& num, int k) {
        int n=num.size();
        int i=k, j=k;
        int mn=num[i];
        int ans=mn;
        while(i!=0 || j!=n-1){
            while(i>0 && min(mn,num[i-1])==mn) i--;
            while(j<n-1 && min(mn,num[j+1])==mn) j++;
            int f = mn*(j-i+1);
            ans = max(ans,f);
            int p=-1;
            if(i>0) p=max(p,min(mn,num[i-1]));
            if(j<n-1) p=max(p,min(mn,num[j+1]));
            mn=p;
        }
        return ans;
    }
};
