class Solution {
    public int n;
    public void nextLeftSmall(int[] nums,int[] arr){
        Stack<Integer>stack = new Stack();
        for(int i = 0;i < n;i++){
            while(!stack.empty() && nums[stack.peek()] >= nums[i])
                stack.pop();
            if(!stack.empty())
                arr[i] = stack.peek();
            else
                arr[i] = -1;
            stack.push(i);
        }
    }

    public void nextRightSmall(int[] nums,int[] arr){
        Stack<Integer>stack = new Stack();
        for(int i = n-1;i >= 0;i--){
            while(!stack.empty() && nums[stack.peek()] >= nums[i])
                stack.pop();
            if(!stack.empty())
                arr[i] = stack.peek();
            else
                arr[i] = n;
            stack.push(i);
        }
    }

    public int maximumScore(int[] nums, int k) {
        n= nums.length;
        int[] arr1 = new int[n];
        int[] arr2 = new int[n];
        nextLeftSmall(nums,arr1);
        nextRightSmall(nums,arr2);

        int ans = 0;
        for(int i = 0;i < n;i++){
            int l = arr1[i];
            int r = arr2[i];

            if(l + 1<= k && k <= r - 1)
                ans = Math.max(ans,nums[i]*(r-l-1));
        }
        return ans;
    }
}
