class Solution {
    public int maximumScore(int[] nums, int k) {
        int i=k-1;
        int j=k+1;
        int n = nums.length;
        int min = nums[k];
        int answer = nums[k];
        while(i>=0 || j< n){
            if(i>=0 && j<n && nums[i] >= nums[j]){
                min = Math.min(min,nums[i]);
                answer = Math.max(answer,(j-i)*min);
                i--;
            }else if(i>=0 && j<n && nums[i] <= nums[j]){
                min = Math.min(min,nums[j]);
                answer = Math.max(answer,(j-i)*min);
                j++;
            }else if(i<0){
                min = Math.min(min,nums[j]);
                answer = Math.max(answer,(j-i)*min);
                j++;
            }else{
                min = Math.min(min,nums[i]);
                answer = Math.max(answer,(j-i)*min);
                i--;
            }
        }
        return answer;
    }
}
