class Solution {
public:
    int maximumScore(vector<int>& nums, int k) {
        int res = 0, cnt, n = nums.size();
        stack<pair<int,int>> st;
        for (int i = 0; i < n; ++ i) {
            cnt = 1;
            while (!st.empty() && st.top().first > nums[i]) {
                cnt += st.top().second;
                if (i > k && (i - cnt + 1) <= k) res = max(res, (cnt - 1) * st.top().first);
                st.pop();
            }
            st.push({nums[i], cnt});
        }
        cnt = 0;
        while (!st.empty()) {
            cnt += st.top().second;
            if ((n - cnt) <= k) res = max(res, cnt * st.top().first);
            st.pop();
        }
        return res;
    }
};
