/**
 * Time Complexity: O(n)
 * Space Complexity: O(1)
 * where `n` is the length of the vector `nums`
 */
class Solution {
 public:
  int maximumScore(const vector<int> &nums, const int k) {
    const int n = static_cast<int>(nums.size());
    int min_num = numeric_limits<int>::max();
    int ret = numeric_limits<int>::min();
    for (int left = k, right = k; true; ) {
      min_num = min({min_num, nums[left], nums[right]});
      ret = max(ret, min_num * (right - left + 1));
      if (left == 0 && right == n - 1) {
        break;
      }

      if (left == 0) {
        ++right;
      } else if (right == n - 1) {
        --left;
      } else {
        if (nums[left - 1] == nums[right + 1]) {
          --left;
          ++right;
        } else if (nums[left - 1] > nums[right + 1]) {
          --left;
        } else {
          ++right;
        }
      }
    }
    return ret;
  }
};
