class Solution {
public:
    int maximumScore(vector<int>& nums, int k) {
        long long int n=nums.size();
        vector< int> pre(n,  INT_MAX);
        //    long long int n=nums.size();
        vector< int> pre2(n-k-1,  INT_MAX);
        pre[k]=nums[k];
        long long int ans=nums[k];
        for(long long int i=k+1; i<n; i++){
            pre[i]=min(pre[i-1], nums[i]);
            ans=max(ans, pre[i]*(i-k+1));
            pre2[n-1-i]=pre[i];
        }
        for(long long int i=k-1; i>=0; i--){
             pre[i]=min(pre[i+1], nums[i]);
              ans=max(ans, pre[i]*(k-i+1));
        }
//   long long int ans=nums[k];
        for(long long int i=n-1; i>k; i--){
            long long int p=pre[i];
            auto it=lower_bound(pre.begin(), pre.begin()+k, p);
            if(it==pre.end()) continue;
            long long int q=it-pre.begin();
            // cout<<i<<" "<<p<<" "<<q<<" "<<p*(i-q+1)<<endl;
            ans=max(ans, p*(i-q+1));
        }
        // for(int i=0; i<pre2.size(); i++) cout<<pre2[i]<<" ";
        for(long long int i=0; i<k; i++){
            long long int p=pre[i];
