class Solution {
public:
    int maximumScore(vector<int>& nums, int k) {
          long long ans = nums[k]; 
          int i = k, j=k;
          int mini = nums[k]; 
          int n = nums.size(); 
          while(i>0 || j<nums.size()-1){
              if(i==0){
                 mini = min(mini,nums[j+1]); 
                 j++;
              }
              else if(j==n-1){
                  mini = min(mini, nums[i-1]); 
                  i--;
              }
              else{
                  if(nums[i-1]>=nums[j+1]){
                      mini = min(nums[i-1], mini); 
                      i--;
                  }
                  else{
                      mini = min(nums[j+1], mini);
                      j++;
                  }
              }
              ans = max(ans,1LL*mini*(j-i+1)); 
          }
          return ans;
    }
};
