class Solution {
public:
    int maximumScore(vector<int>& A, int k) {
        int i = k - 1, j = k + 1, n = A.size(), ans = A[k], mn = A[k]; 

        while(i>=0 || j<n) {
            int lmin = i >= 0 ? A[i] : 0; 
            int rmin = j < n ? A[j] : 0; 
            mn = min(mn, max(lmin, rmin));

            while (i>=0 && A[i]>=mn) i--;
            while (j<n && A[j]>=mn) j++;

            ans = max(ans, mn * (j-i-1));
        }
        return ans;
    }
};
