class Solution {
public:
    vector<int> nextSmallerToRight(vector<int>& nums)
   {
       int n=nums.size();
       vector<int>v(n);
       stack<int>st;
       v[n-1]=n;
       st.push(n-1);
       for(int i=n-2;i>=0;i--)
       {
           while(!st.empty() and nums[i]<=nums[st.top()])
           {
               st.pop();
           }
           if(st.empty())
           {
               v[i]=n;
           }
           else v[i]=st.top();
           st.push(i);
       }
       return v;
   }

   vector<int> nextSmallerToLeft(vector<int>& nums)
   {
       int n=nums.size();
       vector<int>v(n);
       stack<int>st;
       v[0]=-1;
       st.push(0);
       for(int i=1;i<n;i++)
       {
           while(!st.empty() and nums[st.top()]>=nums[i])
           {
               st.pop();
           }
           if(st.empty())
           {
               v[i]=-1;
           }
           else v[i]=st.top();
           st.push(i);
       }
       return v;
   }
    int maximumScore(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>nsl(n),nsr(n);
        nsl=nextSmallerToLeft(nums);
        nsr=nextSmallerToRight(nums);
        int ma=0,le,ri;
        for(int i=0;i<n;i++)
        {
            int val=(nsr[i]-nsl[i]-1);
            if(k<=nsr[i]-1 and k>=nsl[i]+1)
            {
                ma=max(ma,val*(nums[i]));
            }
        }
        return ma;
    }
};
