class Solution:
    def maximumScore(self, nums: List[int], k: int) -> int:

        n = len(nums)
        min_to_k = [max(nums)] * n

        curr = nums[k]
        for i in range(k, -1, -1):
            curr = min(curr, nums[i])
            min_to_k[i] = curr
        curr = nums[k]
        for i in range(k, n):
            curr = min(curr, nums[i])
            min_to_k[i] = curr

        left_memo = [0] * (k+1)
        right = n-1
        for i in range(k+1):
            while min_to_k[i] > min_to_k[right]:
                right -= 1
            left_memo[i] = right

        right_memo = [0] * (n-k)
        left = 0
        for i in range(n-1, k-1, -1):
            while min_to_k[i] > min_to_k[left]:
                left += 1
            right_memo[i-k] = left

        res = 0
        for i in range(k+1):
            res = max(res, min_to_k[i] * (left_memo[i]-i+1))
        for i in range(k, n):
            res = max(res, min_to_k[i] * (i-right_memo[i-k]+1))

        return res

