class Solution {
    public int maximumScore(int[] nums, int k) {
        int n = nums.length;
        int[] left = new int[n];
        int[] right = new int[n];

        right[0] = n;
        Stack<Integer> st = new Stack<>();
        st.push(0);
        for(int i = 1; i < n; i++){
            while(st.size() > 0 && nums[i] < nums[st.peek()]){
                    right[st.pop()] = i;
            }
           st.push(i);
        }

        while(st.size() > 0){
            right[st.pop()] = n;
        }

        left[n - 1] = -1;
        st.clear();
        st.push(n - 1);
        for(int i = n - 2; i >= 0; i--){
            while(st.size() > 0 && nums[i] < nums[st.peek()]){
                    left[st.pop()] = i;
            }
           st.push(i);
        }

        while(st.size() > 0){
            left[st.pop()] = -1;
        }
        int max = Integer.MIN_VALUE;
        for(int i = 0; i < n; i++){
            int maxL = left[i] + 1;
            int maxR = right[i] - 1;
            if(maxL <= k && maxR >= k){
                int len = Math.abs(maxR - maxL) + 1;
                max = Math.max(max, len*nums[i]);
            }
        }
        return max;
    }
}
