//Approach-3 (Using set instead of map in approach-2)
class Solution {
public:
    int secondHighest(string s) {
        set<int> st;

        for(const char &ch : s) {
            if(isdigit(ch))
                st.insert(ch-'0');
        }

        if(st.empty()) return -1;
        auto it = rbegin(st);
        it++;
        return it == rend(st) ? -1 : *it;
    }
};
```

