class Solution {
public:
    int secondHighest(string s) {

        int i = 0;
        int maxi = -1;
        int maxi2 = -1;
        int c = -1;

        while(i<s.size()){
            if(s[i] >= '0' && s[i] <= '9'){
                c = s[i];
                maxi = max(c,maxi);
            }
            i++;
        }

        i = 0;
        while(i<s.size()){
            if(s[i] >= '0' && s[i] <= '9'){
                c = s[i];
                if(c != maxi)
                    maxi2 = max(c,maxi2);
            }
            i++;
        }

        if(maxi2 == -1){
            return maxi2;
        }

        int ans = (maxi2 - '0');
        return ans;

    }
};
```

