class Solution:
    def secondHighest(self, s: str) -> int:
        res = set()

        #store all the digits in set
        for i in s:
            if i.isnumeric():
                res.add(int(i))

        if len(res) <= 1:
            return -1

        #find the second largest element
        first = second = float(-inf)

        res = list(res)

        for i in range(len(res)):
            #if the current element is smaller than first then update both first and second
            if res[i] > first:
                second = first
                first = res[i]

            #if the current element is in between then update second
            elif res[i] > second and res[i] != first:
                second = res[i]

        if second == float(-inf):
            return -1
        else:
            return second

