class Solution(object):
    def secondHighest(self, s):
        """
        :type s: str
        :rtype: int
        """
        sl = []
        for i in s:
           if i.isnumeric():
             sl.append(i)
        try:
            sl = set(sl)
            sl.remove(max(sl))
            return max(sl)
        except ValueError:
            return -1
