class Solution {
    public int secondHighest(String s) {
        Set<Integer> set = new HashSet<>();

        for(char c: s.toCharArray()){
            if(Character.isDigit(c))
            {
                int num = c - '0';
                set.add(num);
            }
        }

        Object[] array = set.toArray();
        if(array.length > 1)
        {
            return (int) array[array.length - 2];
        }
        return -1;
    }
}
