            value = Character.getNumericValue(s.charAt(i)) ;
            if( Character.isDigit(s.charAt(i)) )
            {
                if( value > largest )
                {
                    secondLargest = largest ;
                    largest = value ;
                }
                else if( value > secondLargest && value < largest )
                {
                    secondLargest = value ;
                }
            }            
        }
        return secondLargest ;
    }
}
