class Solution:
    def secondHighest(self, s: str) -> int:
        hi1 = '/'              # first highest digit, initialized to less than zero character
        hi2 = '/'              # second highest digit
        for c in s:
            if c.isdigit():
                if c > hi1:
                    hi1, hi2 = c, hi1
                elif hi2 < c < hi1:
                    hi2 = c
        return int(hi2) if hi2 != '/' else -1
