class Solution {
    public int secondHighest(String s) {
        int len = s.length();
		List<Integer> nums = new ArrayList<>();
		for (int i = 0; i < len; i++) {
			if (Character.isDigit(s.charAt(i)))
				nums.add(s.charAt(i) - '0');
		}
		int first = -1, second = -1;
		for (int i : nums) {
			if (i > first) {
				second = first;
				first = i;
			} else if (i > second && i != first)
				second = i;
		}
		return (second == -1) ? -1 : second;
    }
}
