class Solution {
public:


    int secondHighest(string s) {

        vector<char> v;

        for(int i=0;i<s.length();i++){

            if(s[i]>='0' && s[i]<='9'){
                auto id=find(v.begin(),v.end(),s[i]);
                if(id==v.end())
                    v.push_back(s[i]);
            }

        }

        sort(v.begin(),v.end(),greater<int>());
        if(v.size()<=1)
            return -1;

        return v[1]-'0';
    }
};

