class Solution {
public:

    int secondHighest(string s) {

        int first_max=INT_MIN,second_max=INT_MIN;

        for(int i=0;i<s.length();i++){
            if((int)s[i]>=0 && (int)s[i]<='9'){

               if(s[i]-'0'>first_max){
                   second_max=first_max;
                   first_max=s[i]-'0';
