        int count = 0;
        bool isPrevTwice = false;
        for(int i = 1; i < nums.Length; i++)
        {
            bool isCurrentTwice = nums[count] == nums[i];
            if(!isCurrentTwice || !isPrevTwice)
                nums[++count] = nums[i];
            isPrevTwice = isCurrentTwice;
        }
        return count + 1;
    }
}
