class Solution {
public:
    int removeDuplicates(vector<int>& arr) 
    {
        int s=0;//starting index of a new element
        int u=0;//updation index
        int n=arr.size();
        while(s<n)
        {
            int e=s;//ending index that will point to the last index of a given element
            int count=1;
            while(e<n-1 && arr[e+1]==arr[e])
            {
                e++;
                count++;
            }
            count=min(count,2);
            while(count--)
                arr[u++]=arr[s];
            s=e+1;
        }
        return u;
    }
};
```

