class Solution {
public:
    int largestPerimeter(vector<int>& nums) {
        //select largest three that can form a triangle
        sort(nums.begin(),nums.end());
        //triangle property -> sum of smaller two > largest
        // first valid triplet from end will give maximum perimeter
        //other can not give maximum answer
        for(int i=nums.size()-1;i>=2;i--)
        {
            int a=nums[i-2];
