class Solution {
    public int largestPerimeter(int[] nums) {
        setMaxAtIndexI(nums.length - 1, nums);
        setMaxAtIndexI(nums.length - 2, nums);
        for (int i = nums.length - 1; i > 1; i--) {
             setMaxAtIndexI(i - 2, nums);
            if (nums[i] < nums[i-1] + nums[i-2]) {
                return nums[i] + nums[i-1] + nums[i-2];
            }
        }
        return 0;
    }
    private void setMaxAtIndexI(int index, int [] nums) {
        int maxValue = 0;
        int maxValueIndex = index;

        for (int i = 0; i <= index; i++) {
            if (maxValue < nums[i]) {
                maxValue = nums[i];
                maxValueIndex = i;
            }
        }
        nums[maxValueIndex] = nums[index];
        nums[index] = maxValue;
    }
}
