```
# IDEA: on first pass count total number of 1s, on second pass keep track of how many 0s and 1s encountered so far
#       and calculate current score (subtract current 1s from total 1s and add current 0s), keep track of maximum
#       O(N) time, O(1) space
#
class Solution:
    def maxScore(self, s: str) -> int:
        curr0, curr1, total1 = 0, 0, 0
        for c in s:
            if c == '1':
                total1 += 1

        maxScore = 0
        for c in s[:-1]:
            if c == '0':
                curr0 += 1
            else:
                curr1 += 1
            maxScore = max(maxScore, curr0 + total1 - curr1)

        return maxScore

