class Solution {
public:
    int maxScore(string s) {
        if(s.length() == 0) return 0;  //base case 
        int total_one = 0;
        int total_zero = 0;
        for(int i = 0; i < s.length(); i++){
            if(s[i] == '1')                // count the total no. of numbers of one present in the array
                total_one++;
        }

        int return_ans = 0;
        for(int i  = 0; i < s.length() -1; i++){     
            if(s[i] == '0'){           // if you find 0 in the array, jus increment the count to make sure that the zero moved to the left subarray
                total_zero++;
            }
            else
                total_one--;        //decrement the total_one (1 counter) to make sure that the 1 moved to the left sub array!
            return_ans = max(return_ans, total_one + total_zero);
        }
        return return_ans;
    }
};
