class Solution {
public:
    int maxScore(string s) {
        int c=0;
        for(auto x:s)
            if(x=='1')
                c++;
        int ans=0;
        for(int i=0;i<s.size()-1;i++){
            c+=(s[i]=='0')?1:-1;
            ans=max(c,ans);
        }
        return ans;
    }
};
```

