        for(sIndex in 1..(sCa.size - 2)) {
            if(sCa[sIndex] == '0') {
                counter0++
                if(max < counter0 + counter1) {
                    max = counter0 + counter1
                }
            } else if(sCa[sIndex] == '1') {
                counter1--
            }
        }

        return max
    }
}
