class Solution {
    public int maxScore(String s) {

        int n = s.length();

        int countRightZeroes = 0;
        int countRightOnes = 0; 

        for(char chr : s.toCharArray()) {
            if(chr == '0') countRightZeroes++;
            else countRightOnes++;
        }

        int countLeftZeroes = 0;
        int countLeftOnes = 0; 

        int maxScore = 0;

        for(int i = 0; i < n - 1; i++) {

            char chr = s.charAt(i);

            if(chr == '0') {
                countRightZeroes--;
                countLeftZeroes++;
            } else {
                countRightOnes--;
                countLeftOnes++;
            }

            maxScore = Math.max(maxScore, countLeftZeroes + countRightOnes);

        }
        return maxScore;
    }
}
