class Solution:
  def maxScore(self, s: str) -> int:
    n = len(s)
    ones = [0] * n
    if s[n - 1] == '1':
      ones[n - 1] = 1
    for i in range(n - 2, -1):
      if s[i] == '1':
        ones[i] = ones[i + 1] + 1
    max_sum = 0
    for i in range(n - 1):
      max_sum = max(max_sum, s.count('0', 0, i) + ones[i + 1])
    return max_sum

