        for iterator in 0..<(sLength - 1) {
            if sArray[iterator] == "0" {
                currentZero = currentZero + 1
            } else {
                currentOne = currentOne + 1
            }

            var temp = currentZero + totalOne - currentOne
            ans = max(ans,temp)
        }
        return ans
    }
}
