public int maxAscendingSum(int[] nums) {
	var sum = 0;
	var maxSum = 0;

	for (int i = 0, prev = 0; i < nums.length; prev = nums[i++])
		if (nums[i] > prev) {
			sum += nums[i];
		} else {
			maxSum = Math.max(maxSum, sum);
			sum = nums[i];
		}

	return Math.max(maxSum, sum);
}
