class Solution {
    public int maxAscendingSum(int[] nums) {
        if(nums.length == 1){
            return nums[0];
        }
        int i = 0, max = 0;
        while(i<nums.length){
            int count = nums[i];
            for(int j = i;j<nums.length-1;j++){
                if(nums[j+1]<=nums[j]){
                    max = Math.max(count, max);
                    break; 
                }
                else{
                    count+= nums[j+1];
                    max = Math.max(max, count);
                }
            }
            i++;
        }
        return max;
    }
}
