class Solution {
public:
    int maxAscendingSum(vector<int>& nums) {
	    // mx for global max sum and curr for local iteration sum
        int mx=0,curr=0;

		// adding lowest value at end to compare for last element
        nums.push_back(1);

		// If its only one element return it
        if(nums.size()==1){
            return nums[0];
        }

        // traverse the array
        for(int i=0;i<nums.size()-1;i++){
		    //compare ith and next element 
			//if its in ascending add
            if(nums[i]<nums[i+1]){
                curr+=nums[i];
            }
            else{
				// else add current and update mx 
                curr+=nums[i];
                mx=max(mx,curr);
				// since its descending update local sum
                curr=0;
            }
        }
		// overall mx sum
        return mx;
    }
};
```

