class Solution:
    def maxAscendingSum(self, nums: List[int]) -> int:
        if not nums:
            return 0
        max_sum = nums[0]
        curr_sum = nums[0]

        for i in range(1, len(nums)):
            if nums[i] <= nums[i - 1]: # A rule violation. We have reached an element that is less than previous so sum must reset.
                curr_sum = nums[i]
            else:
                curr_sum += nums[i]

            max_sum = max(max_sum, curr_sum)
        return max_sum
