class Solution:
    def maxAscendingSum(self, nums: List[int]) -> int:
        total = maxSoFar = 0
        last = nums[0] - 1
        for x in nums:
            if x > last:
                total += x
            else:
                maxSoFar = max(maxSoFar, total)
                total = x
            last = x
        maxSoFar = max(maxSoFar, total)
        return maxSoFar
