from itertools import pairwise
from math import inf


class Solution:
    def maxAscendingSum(self, A: list[int]) -> int:
        # return self.method1(A)
        return self.method2(A)

    @staticmethod
    def method2(A: list[int]) -> int:
        A = A + [-inf]

        w_sum, output = 0, 0

        for cur, nxt in pairwise(A):
            w_sum += cur

            if cur >= nxt:
                output = max(output, w_sum)
                w_sum = 0

        return output

    @staticmethod
    def method1(A: list[int]) -> int:
        A = A + [-inf]

        w_sum, output = A[0], A[0]

        for prev, cur in pairwise(A):
            if prev >= cur:
                output = max(output, w_sum)
                w_sum = 0

            w_sum += cur

        return output
```

