    def maxAscendingSum(self, nums: List[int]) -> int:
        maxsum = 0
        l = 0
        while l < len(nums):
            r = l + 1
            currsum = nums[l]
            while r < len(nums):
                if nums[r] <= nums[r-1]:
                    break
                elif nums[r] > nums[r-1]:
                    currsum += nums[r]
                r += 1

            maxsum = max(maxsum, currsum)
            l += 1

        return maxsum
