public class Solution {
    public int MaxAscendingSum(int[] nums) {
            var currSum = nums[0];
            var maxSum = nums[0];
            var allSums = new List<int>();

            for (int i = 1; i < nums.Length; i++)
            {
                if (nums[i] <= nums[i - 1])
                {
                    allSums.Add(maxSum);
                    maxSum = 0;
                    currSum = 0;
                }

                currSum = Math.Max(nums[i], nums[i] + currSum);
                maxSum = Math.Max(maxSum, currSum);
            }

            allSums.Add(maxSum);
            allSums.Sort();
            var test = allSums.Last();

            return test;
    }
}
```

